export default (state, action) => {
    switch (action.type) {

        case 'GET_SPECS':
            return {
                ...state,
                loading: false,
                specs: action.payload
              }


        case 'UPDATE_ABOUT':
            return {
                ...state,
                about: action.payload
            };


        case 'UPDATE_DRIVERS':
            return {
                ...state,
                drivers: state.drivers.map(driver =>
                    driver.id === action.payload.id ? action.payload : driver
                  )
            }; 

        case 'UPDATE_SPECS':
            return {
                ...state,
                specs: {
                    ...state.specs,
                    ...action.payload
                }
            };

        default:
            return state;
    }
}
