import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../admin-style/News.css';

const News = () => {
    const [news, setNews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [editMode, setEditMode] = useState(false);
    const [editedNews, setEditedNews] = useState({});

    useEffect(() => {
        const fetchNews = async () => {
            try {
                const response = await axios.get('/api/v1/news');
                setNews(response.data.data);
                setLoading(false);
            } catch (err) {
                setError('Error fetching news');
                setLoading(false);
            }
        };

        fetchNews();
    }, []);

    const handleChange = (e, index) => {
        const { name, value } = e.target;
        setEditedNews((prevEditedNews) => ({
            ...prevEditedNews,
            [index]: {
                ...prevEditedNews[index],
                [name]: value,
            },
        }));
    };

    const handleEdit = () => {
        setEditMode(true);
        const initialEditedNews = {};
        news.forEach((item, index) => {
            initialEditedNews[index] = { ...item };
        });
        setEditedNews(initialEditedNews);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await Promise.all(
                Object.keys(editedNews).map(async (index) => {
                    const response = await axios.put(`/api/v1/news/${news[index]._id}`, editedNews[index]);
                    return response.data;
                })
            );
            alert('News updated successfully!');
            window.location.reload();
        } catch (err) {
            console.error('Error updating news:', err);
            setError(`Error updating news: ${err.message}`);
        }
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (error) {
        return <div className="error">{error}</div>;
    }

    return (
        <div className="news-container">
            {!editMode && (
                <div>
                    <h2 className="news-title">News</h2>
                    {news.map((item, index) => (
                        <div key={index} className="news-item">
                            <h3 className="news-heading">News {index + 1}</h3>
                            <p className="news-detail">Title: {item.title}</p>
                            <p className="news-detail">Caption: {item.caption}</p>
                            <hr className="divider" />
                        </div>
                    ))}
                    <button className="edit-button" onClick={handleEdit}>Edit</button>
                </div>
            )}

            {editMode && (
                <div>
                    <h2 className="edit-title">Edit News</h2>
                    <form onSubmit={handleSubmit}>
                        {news.map((item, index) => (
                            <div key={index} className="edit-news">
                                <h3 className="edit-heading">Edit News {index + 1}</h3>
                                <label htmlFor={`title${index}`} className="edit-label">Title:</label>
                                <input
                                    type="text"
                                    id={`title${index}`}
                                    name="title"
                                    value={editedNews[index]?.title || item.title}
                                    onChange={(e) => handleChange(e, index)}
                                    className="edit-input"
                                />
                                <br />
                                <label htmlFor={`caption${index}`} className="edit-label">Caption:</label>
                                <input
                                    type="text"
                                    id={`caption${index}`}
                                    name="caption"
                                    value={editedNews[index]?.caption || item.caption}
                                    onChange={(e) => handleChange(e, index)}
                                    className="edit-input"
                                />
                                <br />
                            </div>
                        ))}
                        <button type="submit" className="update-button">Update News</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default News;
