import React, {createContext, useReducer} from 'react';
import AppReducer from './AppReducer'

//initial state

const driver= [
    {id: 1, name: 'CHARLES LECLERC', wins: 4, podiums: 20, pole: 24, last_w: 'MONZA 19'},
    {id: 2, name: 'CARLOS SAINZ', wins: 2, podiums: 7, pole: 3, last_w: 'BAHRAIN 22'}
];

const initialState = {

    news: [
        {id: 1, title: "IT'S HAMMER TIME!", caption:"Lewis HAMILTION to drive for ferrari starting 2025."},
        {id: 2, title: "FERRAR IN BLUE", caption:"Scuderia Ferrari reveals new blue livery ahead of Miami GP."},
        {id: 3, title: "BACK TO THE PODIUM!", caption:"Leclerc steals the third spot in Miami."},
    ],

    specs: {
        id: 1, 
        horsepower: '747 HP',
        engine: 'V6',
        energy: '4 MJ',
        displacement: '1600 cc',
        power: '120 kW'
    },
   
    drivers: driver,   

    team: {
        id: 1,
        wins: (driver[0].wins + driver[1].wins),
        podiums: (driver[0].podiums + driver[1].podiums),
        pole: (driver[0].pole + driver[1].pole),
        last_w: driver[0].last_w+", "+driver[1].last_w
    },

    about: {
        text: 'Scuderia Ferrari, currently competing as Scuderia Ferrari HP, is the racing division of luxury Italian auto manufacturer Ferrari and the racing team that competes in Formula One racing. The team is also known by the nickname '+'"The Prancing Horse"'+", in reference to their logo. It is the oldest surviving and most successful Formula One team, having competed in every world championship since 1950."
    },

    

}

//Context
export const GlobalContext = createContext(initialState);



//Provider Component
export const GlobalProvider = ({children}) => {
    const [state,dispatch] = useReducer(AppReducer, initialState);

    const updateSpecs = (specs) => {
        dispatch({ type: 'UPDATE_SPECS', payload: specs });
    }

    const updateDrivers = (drivers) => {
        dispatch({ type: 'UPDATE_DRIVERS', payload: drivers});
    }

    const updateAbout = (about) => {
        dispatch({ type: 'UPDATE_ABOUT', payload: about});
    }

    return (<GlobalContext.Provider value={{
        news: state.news,
        specs: state.specs, updateSpecs,
        drivers: state.drivers, updateDrivers,
        team: state.team,
        about: state.about, updateAbout
    }}>
        {children}
    </GlobalContext.Provider>);
}
