import React, { useState, useEffect } from 'react';
import axios from 'axios';

const News = () => {
    const [news, setNews] = useState([]);
    const [selectedNews, setSelectedNews] = useState(null);
    const [updatedTitle, setUpdatedTitle] = useState('');

    useEffect(() => {
        const fetchNews = async () => {
            try {
                const response = await axios.get('/api/news'); // Adjust the route according to your backend setup
                setNews(response.data.data);
            } catch (error) {
                console.error('Error fetching news:', error);
            }
        };
        fetchNews();
    }, []);

    const selectNews = (item) => {
        setSelectedNews(item);
        setUpdatedTitle(item.title);
    };

    const handleChange = (e) => {
        setUpdatedTitle(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`/api/news/${selectedNews._id}`, { title: updatedTitle }); // Adjust the route according to your backend setup
            alert('News title updated successfully!');
            // Refresh the news list after updating
            const response = await axios.get('/api/news'); // Fetch the updated news list
            setNews(response.data.data);
        } catch (error) {
            console.error('Error updating news title:', error);
        }
    };

    return (
        <div>
            <h2>Update News Title</h2>
            <ul>
                {news.map(item => (
                    <li key={item._id} onClick={() => selectNews(item)}>
                        {item.title}
                    </li>
                ))}
            </ul>
            {selectedNews && (
                <form onSubmit={handleSubmit}>
                    <div>
                        <label htmlFor="updatedTitle">Updated Title:</label>
                        <input
                            type="text"
                            id="updatedTitle"
                            value={updatedTitle}
                            onChange={handleChange}
                        />
                    </div>
                    <button type="submit">Update Title</button>
                </form>
            )}
        </div>
    );
};

export default News;
