import React, { useState, useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';
import '../admin-style/CarSpecs.css';

const CarSpecs = () => {
  const { specs, updateSpecs } = useContext(GlobalContext);

  const [horsepower, setHorsepower] = useState(specs.horsepower);
  const [engine, setEngine] = useState(specs.engine);
  const [energy, setEnergy] = useState(specs.energy);
  const [displacement, setDisplacement] = useState(specs.displacement);
  const [power, setPower] = useState(specs.power);

  const onSubmit = (e) => {
    e.preventDefault();

    const updatedSpecs = {
      id: specs._id,
      horsepower,
      engine,
      energy,
      displacement,
      power
    };

    updateSpecs(updatedSpecs);
  };

  return (
    <>
      <h3>Update Car Specifications</h3>
      <form onSubmit={onSubmit}>
        <div className="form-control">
          <label htmlFor="horsepower">Horsepower</label>
          <input
            type="text"
            value={horsepower}
            onChange={(e) => setHorsepower(e.target.value)}
            placeholder="Enter horsepower..."
          />
        </div>
        <div className="form-control">
          <label htmlFor="engine">Engine</label>
          <input
            type="text"
            value={engine}
            onChange={(e) => setEngine(e.target.value)}
            placeholder="Enter engine..."
          />
        </div>
        <div className="form-control">
          <label htmlFor="energy">Battery Energy</label>
          <input
            type="text"
            value={energy}
            onChange={(e) => setEnergy(e.target.value)}
            placeholder="Enter battery energy..."
          />
        </div>
        <div className="form-control">
          <label htmlFor="displacement">Total Displacement</label>
          <input
            type="text"
            value={displacement}
            onChange={(e) => setDisplacement(e.target.value)}
            placeholder="Enter total displacement..."
          />
        </div>
        <div className="form-control">
          <label htmlFor="power">MGU-K Power</label>
          <input
            type="text"
            value={power}
            onChange={(e) => setPower(e.target.value)}
            placeholder="Enter MGU-K power..."
          />
        </div>
        <button className="btn">Update Specifications</button>
      </form>

    </>
  );
};

export default CarSpecs;
