import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UpdateSpecsComponent = () => {
    const [specs, setSpecs] = useState({
        horsepower: '',
        engine: '',
        energy: '',
        displacement: '',
        power: ''
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(false);
    const id = '6650f593f6b860f49ded431a';

    useEffect(() => {
        const fetchSpecs = async () => {
            try {
                const response = await axios.get(`/api/v1/specs/${id}`);
                setSpecs(response.data.data);
                setLoading(false);
            } catch (err) {
                setError('Error fetching specs');
                setLoading(false);
            }
        };

        fetchSpecs();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSpecs((prevSpecs) => ({
            ...prevSpecs,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`/api/v1/specs/${id}`, specs);
            setSuccess(true);
        } catch (err) {
            setError('Error updating specs');
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Update Specs</h2>
            {success && <div>Specs updated successfully!</div>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="horsepower">Horsepower:</label>
                    <input
                        type="text"
                        id="horsepower"
                        name="horsepower"
                        value={specs.horsepower}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label htmlFor="engine">Engine:</label>
                    <input
                        type="text"
                        id="engine"
                        name="engine"
                        value={specs.engine}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label htmlFor="energy">Energy:</label>
                    <input
                        type="text"
                        id="energy"
                        name="energy"
                        value={specs.energy}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label htmlFor="displacement">Displacement:</label>
                    <input
                        type="text"
                        id="displacement"
                        name="displacement"
                        value={specs.displacement}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label htmlFor="power">Power:</label>
                    <input
                        type="text"
                        id="power"
                        name="power"
                        value={specs.power}
                        onChange={handleChange}
                    />
                </div>
                <button type="submit">Update Specs</button>
            </form>
        </div>
    );
};

export default UpdateSpecsComponent;
