import React, { useState } from 'react';
import CL from "../images/leclerc.png";
import CS from "../images/sainz.png";
import NUM16 from "../images/16.png";
import MONACO from "../images/monaco.png";

const Drivers = () => {
  const [showLeclercInfo, setShowLeclercInfo] = useState(false);

  const handleMouseOver = () => {
    setShowLeclercInfo(true);
  };

  const handleMouseOut = () => {
    setShowLeclercInfo(false);
  };

  return (
    <div style={{ height: "100vh" }} id='drivers'>
      <img src={CL} alt='leclerc' id='leclerc' />

      {/* Show Leclerc info when mouse is over the leclerc image */}
      {showLeclercInfo && (
        <div className='leclerc-info'>
          <img src={NUM16} alt='num16' id='num16' />
          <h2>Charles Leclerc</h2>
          <img src={MONACO} alt='monaco-flag' id='monaco' style={{ height: "30px" }} />
        </div>
      )}

      <img src={CS} alt='sainz' id='sainz' />

      {/* Use onMouseOver and onMouseOut event handlers */}
      <div className='leclerc-info' style={{ display: "none" }} onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
        <img src={NUM16} alt='num16' id='num16' />
        <h2>Charles Leclerc</h2>
        <img src={MONACO} alt='monaco-flag' id='monaco' style={{ height: "30px" }} />
      </div>

      <div className='sainz-info'>v</div>
    </div>
  );
};

export default Drivers;
