import React, { useState, useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';


function AboutUs() {
  const { aboutText, dispatch } = useContext(GlobalContext);
  const [editedAboutText, setEditedAboutText] = useState(aboutText);
  const [isEditing, setIsEditing] = useState(false);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    setIsEditing(false);
    // Dispatch the action to update the about text in the global state
    dispatch(updateAboutText(editedAboutText));
  };

  const handleChange = (e) => {
    setEditedAboutText(e.target.value);
  };

  return (
    <div className="about-us-container">
      <h2>About Us</h2>
      <div className="about-us-content">
        {isEditing ? (
          <>
            <textarea
              value={editedAboutText}
              onChange={handleChange}
              className="about-us-textarea"
            />
            <button onClick={handleSave} className="about-us-button">Save</button>
          </>
        ) : (
          <>
            <p>{aboutText}</p>
            <button onClick={handleEdit} className="about-us-button">Edit</button>
          </>
        )}
      </div>
    </div>
  );
}

export default AboutUs;
