import React, { useContext, useState } from 'react';
import { GlobalContext } from '../context/GlobalState';
import '../admin-style/DriversStats.css';

function DriversStats() {
  const { drivers, updateDrivers } = useContext(GlobalContext);
  const [editedDriver, setEditedDriver] = useState({});
  
  const handleEdit = (driver) => {
    setEditedDriver({ ...driver });
  };

  const handleSave = () => {
    updateDrivers(editedDriver);
    setEditedDriver({});
  };

  const handleChange = (e, field) => {
    setEditedDriver({ ...editedDriver, [field]: e.target.value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setEditedDriver({ ...editedDriver, image: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="drivers-stats-container">
      <h2>Driver Stats</h2>
      {drivers.map((driver) => (
        <div className="drivers-stats-item" key={driver.id}>
          <img src={driver.image} alt="Driver" className="driver-image" />
          <div className="drivers-stats-content">
            {editedDriver.id === driver.id ? (
              // Edit mode
              <form>
                <input
                  type="text"
                  value={editedDriver.name || ''}
                  onChange={(e) => handleChange(e, 'name')}
                  placeholder="Name"
                />
                <input
                  type="number"
                  value={editedDriver.wins || ''}
                  onChange={(e) => handleChange(e, 'wins')}
                  placeholder="Wins"
                />
                <input
                  type="number"
                  value={editedDriver.podiums || ''}
                  onChange={(e) => handleChange(e, 'podiums')}
                  placeholder="Podiums"
                />
                <input
                  type="number"
                  value={editedDriver.pole || ''}
                  onChange={(e) => handleChange(e, 'pole')}
                  placeholder="Pole Positions"
                />
                <input
                  type="text"
                  value={editedDriver.lastWin || ''}
                  onChange={(e) => handleChange(e, 'lastWin')}
                  placeholder="Last Win"
                />
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                />
                <button type="button" onClick={handleSave}>Save</button>
              </form>
            ) : (
              // View mode
              <>
                <ul>
                  <h3>{driver.name}</h3>
                  <li>Wins: {driver.wins}</li>
                  <li>Podiums: {driver.podiums}</li>
                  <li>Pole Positions: {driver.pole}</li>
                  <li>Last Win: {driver.lastWin}</li>
                </ul>
                <button onClick={() => handleEdit(driver)}>Edit</button>
              </>
            )}
          </div>
        </div>
        
      ))}
      <h3>{drivers[0].last_w}</h3>
    </div>
  );
}

export default DriversStats;
