import React, { useContext, useState } from 'react';
import { GlobalContext } from '../context/GlobalState';
import '../admin-style/CarSpecs.css';

const CarSpecs = () => {
  const { specs, updateSpecs } = useContext(GlobalContext);

  const [carSpecs, setCarSpecs] = useState([specs]);
  const [isEditing, setIsEditing] = useState(null);

  const handleEdit = (index) => {
    setIsEditing(index);
  };

  const handleSave = () => {
    updateSpecs(carSpecs[0]);
    setIsEditing(null);
  };

  const handleChange = (e, index, field) => {
    const newCarSpecs = [...carSpecs];
    newCarSpecs[index][field] = e.target.value;
    setCarSpecs(newCarSpecs);
  };

  return (
    <div className="car-specs-container">
      <h2>Car Specifications</h2>
      {carSpecs.map((item, index) => (
        <div key={index} className="car-spec-item">
          <div className="car-spec-content">
            {isEditing === index ? (
              <>
                <h3>{item.title}</h3>
                <input
                  type="text"
                  value={item.horsepower}
                  onChange={(e) => handleChange(e, index, 'horsepower')}
                />
                <input
                  type="text"
                  value={item.engine}
                  onChange={(e) => handleChange(e, index, 'engine')}
                />
                <input
                  type="text"
                  value={item.energy}
                  onChange={(e) => handleChange(e, index, 'energy')}
                />
                <input
                  type="text"
                  value={item.displacement}
                  onChange={(e) => handleChange(e, index, 'displacement')}
                />
                <input
                  type="text"
                  value={item.power}
                  onChange={(e) => handleChange(e, index, 'power')}
                />
                <button onClick={handleSave}>Save</button>
              </>
            ) : (
              <>
                <h3>{item.title}</h3>
                <p>Horsepower: {item.horsepower}</p>
                <p>Engine: {item.engine}</p>
                <p>Battery Energy: {item.energy}</p>
                <p>Total Displacement: {item.displacement}</p>
                <p>MGU-K Power: {item.power}</p>
                <button onClick={() => handleEdit(index)}>Edit</button>
              </>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default CarSpecs;
