import React, {createContext, useReducer} from 'react';
import AppReducer from './AppReducer'

//initial state

const initialDrivers = [
    { id: 1, name: 'CHARLES LECLERC', wins: 4, podiums: 20, pole: 24, last_w: '2019 MONZA GP' },
    { id: 2, name: 'CARLOS SAINZ', wins: 2, podiums: 7, pole: 3, last_w: '2022 BAHRAIN GP' }
  ];
  
const initialState = {
    specs: {
        id: 1, 
        horsepower: '747 HP',
        engine: 'V6',
        energy: '4 MJ',
        displacement: '1600 cc',
        power: '120 kW'
    },

    driver: [
        {id: 1, name: 'CHARLES LECLERC', wins: 4, podiums: 20, pole: 24, last_w: '2019 MONZA GP'},
        {id: 1, name: 'CARLOS SAINZ', wins: 2, podiums: 7, pole: 3, last_w: '2022 BAHRAIN GP'}
    ],    

    team: {
        id: 1,
        wins: (driver[0].wins + driver[1].wins), podiums: 20, pole: 24, last_w: '2019 MONZA GP'
    },


}

//Context
export const GlobalContext = createContext(initialState);

//Provider Component
export const GlobalProvider = ({children}) => {
    const [state,dispatch] = useReducer(AppReducer, initialState);

    return (<GlobalContext.Provider value={{
        specs: state.specs
    }}>
        {children}
    </GlobalContext.Provider>);
}
