import React, { useState, useContext, useEffect } from 'react';
import { GlobalContext } from '../context/GlobalState';
import axios from 'axios';

function CarSpecs() {
  const { specs, updateSpecs } = useContext(GlobalContext);
  const [editedSpecs, setEditedSpecs] = useState(specs);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const fetchSpecs = async () => {
      try {
        const res = await axios.get('/api/v1/specs');
        setEditedSpecs(res.data); // Assuming the response contains the specs
        updateSpecs(res.data); // Update the global state
      } catch (error) {
        console.error('Failed to fetch specs', error);
      }
    };

    fetchSpecs();
  }, []); // Empty dependency array ensures this runs only once when the component mounts

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = async () => {
    setIsEditing(false);
    try {
      await axios.put(`/api/v1/specs/${editedSpecs.id}`, editedSpecs);
      updateSpecs(editedSpecs);
    } catch (error) {
      console.error('Failed to save specs', error);
    }
  };

  const handleChange = (e, field) => {
    setEditedSpecs({ ...editedSpecs, [field]: e.target.value });
  };

  return (
    <div className="car-specs-container">
      <h2>Car Specs</h2>
      <div className="car-specs-content">
        {isEditing ? (
          <>
            <label htmlFor="horsepower">Horsepower</label>
            <input
              type="text"
              id="horsepower"
              value={editedSpecs.horsepower}
              onChange={(e) => handleChange(e, 'horsepower')}
            />
            <label htmlFor="engine">Engine</label>
            <input
              type="text"
              id="engine"
              value={editedSpecs.engine}
              onChange={(e) => handleChange(e, 'engine')}
            />
            <label htmlFor="energy">Battery Energy</label>
            <input
              type="text"
              id="energy"
              value={editedSpecs.energy}
              onChange={(e) => handleChange(e, 'energy')}
            />
            <label htmlFor="displacement">Total Displacement</label>
            <input
              type="text"
              id="displacement"
              value={editedSpecs.displacement}
              onChange={(e) => handleChange(e, 'displacement')}
            />
            <label htmlFor="power">MGU-K Power</label>
            <input
              type="text"
              id="power"
              value={editedSpecs.power}
              onChange={(e) => handleChange(e, 'power')}
            />
            <button onClick={handleSave} className="car-specs-button">Save</button>
          </>
        ) : (
          <>
            <ul>
              <li>Horsepower: {specs.horsepower}</li>
              <li>Engine: {specs.engine}</li>
              <li>Battery Energy: {specs.energy}</li>
              <li>Total Displacement: {specs.displacement}</li>
              <li>MGU-K Power: {specs.power}</li>
            </ul>
            <button onClick={handleEdit} className="car-specs-button">Edit</button>
          </>
        )}
      </div>
    </div>
  );
}

export default CarSpecs;
