import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AboutUs = () => {
    const [about, setAbout] = useState('');
    const [editMode, setEditMode] = useState(false);
    const [updatedAbout, setUpdatedAbout] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchAbout = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/v1/about');
                setAbout(response.data.data[0].text); // Assuming only one about text exists
                setLoading(false);
            } catch (error) {
                setError('Error fetching about text');
                setLoading(false);
            }
        };

        fetchAbout();
    }, []);

    const handleEditClick = () => {
        setUpdatedAbout(about);
        setEditMode(true);
    };

    const handleChange = (e) => {
        setUpdatedAbout(e.target.value);
    };

    const handleUpdate = async () => {
        try {
            const response = await axios.put('http://localhost:5000/api/v1/about', { text: updatedAbout });
            setAbout(response.data.data.text);
            setEditMode(false);
        } catch (error) {
            setError('Error updating about text');
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className='about-us-container'>
            {editMode ? (
                <div>
                    <textarea value={updatedAbout} onChange={handleChange} />
                    <button onClick={handleUpdate}>Update</button>
                </div>
            ) : (
                <div>
                    <p>{about}</p>
                    <button onClick={handleEditClick}>Edit</button>
                </div>
            )}
        </div>
    );
};

export default AboutUs;
