import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NewsComponent = () => {
    const [news, setNews] = useState([]);
    const [updatedTitle, setUpdatedTitle] = useState('');
    const [selectedNews, setSelectedNews] = useState(null);

    // Fetch news data from the server
    useEffect(() => {
        const fetchNews = async () => {
            try {
                const response = await axios.get('/api/news'); // Adjust the route according to your backend setup
                setNews(response.data.data);
            } catch (error) {
                console.error('Error fetching news:', error);
            }
        };
        fetchNews();
    }, []);

    // Function to handle updating the news title
    const handleUpdateTitle = async () => {
        try {
            const response = await axios.put(`/api/news/${selectedNews._id}`, { title: updatedTitle }); // Adjust the route according to your backend setup
            console.log('Updated news:', response.data.data);
            // Update the news list with the updated news
            const updatedNews = news.map(item => (item._id === response.data.data._id ? response.data.data : item));
            setNews(updatedNews);
            // Clear the selected news and updated title
            setSelectedNews(null);
            setUpdatedTitle('');
        } catch (error) {
            console.error('Error updating news:', error);
        }
    };

    // Function to select a news item for updating
    const selectNews = (item) => {
        setSelectedNews(item);
        setUpdatedTitle(item.title);
    };

    return (
        <div>
            <h1>News</h1>
            <ul>
                {news.map(item => (
                    <li key={item._id} onClick={() => selectNews(item)}>
                        {item.title}
                    </li>
                ))}
            </ul>
            {selectedNews && (
                <div>
                    <h2>Update News Title</h2>
                    <input
                        type="text"
                        value={updatedTitle}
                        onChange={(e) => setUpdatedTitle(e.target.value)}
                    />
                    <button onClick={handleUpdateTitle}>Update Title</button>
                </div>
            )}
        </div>
    );
};

export default NewsComponent;
