import React, { useState, useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';
import axios from 'axios';

const CarSpecs = () => {
    const { specs, updateSpecs } = useContext(GlobalContext);
    const [editedSpecs, setEditedSpecs] = useState(specs);
    const [isEditing, setIsEditing] = useState(false);

    const handleEdit = () => {
        setIsEditing(true);
    };

    const handleSave = async () => {
        setIsEditing(false);
        // Update specs in the global state
        const updatedSpecs = await updateSpecs(editedSpecs);
        // Optionally update local state if needed
        setEditedSpecs(updatedSpecs);
    };

    const handleChange = (e, field) => {
        setEditedSpecs({ ...editedSpecs, [field]: e.target.value });
    };

    return (
        <div className="car-specs-container">
            <h2>Car Specs</h2>
            <div className="car-specs-content">
                {isEditing ? (
                    <>
                        {/* Input fields for editing */}
                        <input
                            type="text"
                            value={editedSpecs.horsepower}
                            onChange={(e) => handleChange(e, 'horsepower')}
                        />
                        <input
                            type="text"
                            value={editedSpecs.engine}
                            onChange={(e) => handleChange(e, 'engine')}
                        />
                        <input
                            type="text"
                            value={editedSpecs.energy}
                            onChange={(e) => handleChange(e, 'energy')}
                        />
                        <input
                            type="text"
                            value={editedSpecs.displacement}
                            onChange={(e) => handleChange(e, 'displacement')}
                        />
                        <input
                            type="text"
                            value={editedSpecs.power}
                            onChange={(e) => handleChange(e, 'power')}
                        />
                        <button onClick={handleSave}>Save</button>
                    </>
                ) : (
                    <>
                        {/* Display current specs */}
                        <p>Horsepower: {specs.horsepower}</p>
                        <p>Engine: {specs.engine}</p>
                        <p>Energy: {specs.energy}</p>
                        <p>Displacement: {specs.displacement}</p>
                        <p>Power: {specs.power}</p>
                        <button onClick={handleEdit}>Edit</button>
                    </>
                )}
            </div>
        </div>
    );
};

export default CarSpecs;
