import React, {createContext, useReducer} from 'react';
import AppReducer from './AppReducer'

//initial state

const driver= [
    {id: 1, name: 'CHARLES LECLERC', wins: 4, podiums: 20, pole: 24, last_w: '19 MONZA'},
    {id: 1, name: 'CARLOS SAINZ', wins: 2, podiums: 7, pole: 3, last_w: '22 BAHRAIN'}
];

const initialState = {
    specs: {
        id: 1, 
        horsepower: '747 HP',
        engine: 'V6',
        energy: '4 MJ',
        displacement: '1600 cc',
        power: '120 kW'
    },
   
    drivers: driver,   

    team: {
        id: 1,
        wins: (driver[0].wins + driver[1].wins), podiums: (driver[0].podiums + driver[1].podiums),
         pole: (driver[0].pole + driver[1].pole), last_w: `${drivers[0].last_w}\n${drivers[1].last_w}`
    },

    

}

//Context
export const GlobalContext = createContext(initialState);

//Provider Component
export const GlobalProvider = ({children}) => {
    const [state,dispatch] = useReducer(AppReducer, initialState);

    return (<GlobalContext.Provider value={{
        specs: state.specs,
        drivers: state.drivers,
        team: state.team
    }}>
        {children}
    </GlobalContext.Provider>);
}
