export default (state, action) => {
    switch (action.type) {

        case 'UPDATE_ABOUT_TEXT':
            return {
                ...state,
                aboutText: action.payload
            };


        case 'UPDATE_DRIVERS':
            return {
                ...state,
                drivers: state.drivers.map(driver =>
                    driver.id === action.payload.id ? action.payload : driver
                  )
            }; 

        case 'UPDATE_SPECS':
            return {
                ...state,
                specs: action.payload
            };

        default:
            return state;
    }
}
